import React, {Component} from "react";

// Heres how React's New Context API Works
// https://www.youtube.com/watch?v=XLJN4JfniH4

const UserContext = React.createContext();

class UserProvider extends Component{
    state = {
        id: "CONTEXT TEST ID",
        firstName: "CONTEXT TEST FIRST NAME",
        lastName: "CONTEXT TEST LAST NAME",
        email: "CONTEXT TEST EMAIL",
        password: "CONTEXT TEST PASSWORD"
    }

    render(){
        return(
            <UserContext.Provider value={{
                state: this.state
            }}>
                {this.props.children}
            </UserContext.Provider>
        );
        
    }
}

/* 
in components to use context data:
    import React, { useContext } from "react";
    import UserContext from "../../utils/UserContext";
    
    const currentUser = useContext(UserContext);
*/

export default UserProvider;
