import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";


class Saved extends Component {
    state = {
        product: {}
    };

    componentDidMount() 
    {
        //console.log("window.location.href = " + window.location.href);
        // http://localhost:3000/store/5d474e210953e00dbc14734e
        var url = window.location.href;
        var urlArray = url.split("/");
        //console.log("urlArray" + urlArray);
        this.loadProduct(urlArray[urlArray.length - 1]);
    }

  loadProduct = (pid) => {
    API.getProduct(pid)
      .then(res => {
          console.log("getProduct response:", res);
            this.setState({ 
                product: res.data
            });
        }
      )
      .catch(err => console.log(err));
  };

    render() {
        return (
            <Container fluid>
                <Row>
                    <Col size="md-2"></Col>
                    <Col size="md-8">
                        <Jumbotron>
                            <h1>Product Page</h1>
                        </Jumbotron>
                        
                        <div><strong>{this.state.product.name}</strong></div>
                        {/*
                        <div>
                            {this.state.addressLine1} <br />
                            {this.state.addressLine2.length ? (
                                <span>
                                    {this.state.addressLine2} <br />
                                </span>
                                ) : (
                                    <span></span>
                                )}
                            {this.state.city}, {this.state.state} {this.state.zip}
                        </div>
                        <div>{this.state.description}</div>

                        <div>
                            <div><strong>Available Products</strong></div>
                            {this.state.products.length ? (
                                <div>
                                    {this.state.products.map(product => (
                                        <div key={product._id}> 
                                            <strong><a href={"product/" + product._id}>{product.name}</a></strong><br />
                                            {product.name}<br />
                                            {product.description}
                                        </div>
                                    ))}
                                </div>
                            ) : (
                                <div>No Products</div>
                            )}
                        </div>
                        */}
                    </Col>
                    <Col size="md-2"></Col>
                </Row>
            </Container>
        );
    }
}

export default Saved;
