import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";


class Saved extends Component {
    state = {
        id: "",
        name: "",
        addressLine1: "",
        addressLine2: "",
        city: "",
        state: "",
        zip: "",
        description: "",
        products: []
    };

    componentDidMount() 
    {
        console.log("window.location.href = " + window.location.href);
        // http://localhost:3000/store/5d474e210953e00dbc14734e
        var url = window.location.href;
        var urlArray = url.split("/");
        console.log("urlArray" + urlArray);
        this.loadStore(urlArray[urlArray.length - 1]);
    }

  loadStore = (storeId) => {
    API.getStore(storeId)
      .then(res => {
          console.log("getStore response:", res);
            this.setState({ 
                id: res.data._id,
                name: res.data.name,
                addressLine1: res.data.addressLine1,
                addressLine2: res.data.addressLine2,
                city: res.data.city,
                state: res.data.state,
                zip: res.data.zip,
                description: res.data.description,
                products: res.data.products
            });
        }
      )
      .catch(err => console.log(err));
  };

    render() {
        return (
            <Container fluid>
                <Row>
                    <Col size="md-2"></Col>
                    <Col size="md-8">
                        <Jumbotron>
                            <h1>Store Page</h1>
                        </Jumbotron>
                        <div><strong>{this.state.name}</strong></div>
                        <div>
                            {this.state.addressLine1} <br />
                            {this.state.addressLine2.length ? (
                                <span>
                                    {this.state.addressLine2} <br />
                                </span>
                                ) : (
                                    <span></span>
                                )}
                            {this.state.city}, {this.state.state} {this.state.zip}
                        </div>
                        <div>{this.state.description}</div>

                        <div>
                            <div><strong>Available Products</strong></div>
                            {this.state.products.length ? (
                                <div>
                                    {this.state.products}
                                </div>
                            ) : (
                                <div>No Products</div>
                            )}
                        </div>


                    </Col>
                    <Col size="md-2"></Col>
                </Row>
            </Container>
        );
    }
}

export default Saved;
